-- restrict to dosage instructions that occur >= 50 times
IF OBJECT_ID('tempdb..#SafeDosages') IS NOT NULL DROP TABLE #SafeDosages;
SELECT Dosage INTO #SafeDosages FROM #medications
group by Dosage
having count(*) >= 50;



