SELECT 
	PatientId = FK_Patient_Link_ID,
	PrescriptionDate,
	concept,
	Description = REPLACE(Description, ',',' '),
	Dosage = LEFT(REPLACE(REPLACE(REPLACE(ISNULL(#SafeDosages.Dosage, 'REDACTED'),',',' '),CHAR(13),' '),CHAR(10),' '),50),
