-- Creates the code set table for this project.
DROP TABLE IF EXISTS SDE_REPOSITORY.SHARED_UTILITIES."Code_Sets_SDE_Lighthouse_06_Chen";
CREATE TABLE SDE_REPOSITORY.SHARED_UTILITIES."Code_Sets_SDE_Lighthouse_06_Chen" (
	CONCEPT VARCHAR(255),
	VERSION NUMBER(38,0),
	TERMINOLOGY VARCHAR(20),
	CODE VARCHAR(20),
	DESCRIPTION VARCHAR(255)
);